mapper.select:
		select t_no, t_submitdate, o_register_no, t_owner_name, t_stores_name, t_stores_address,
       		   t_stores_address_detail, t_owner_hp1, t_owner_hp2, t_owner_hp3, t_detail, a_agree_no 
       		   from temporary where t_no=#{no}

update temporary
set a_agree_no = 3
where t_no =#{no}

update temporary
set a_agree_no = 4
where t_no =#{no}
