mapper.select:
select BOARD_TITLE, BOARD_CONTENT, BOARD_THUMBNAIL, BOARD_REGDATE,
BOARD_HEAD, AUTHORITY_NO from BOARD
where BOARD_NO=#{boardNo}

mapper.update:
update BOARD
set BOARDP_TITLE=#{boardTitle}, BOARD_CONTENT=#{boardContent}, BOARD_THUMBNAIL=#{boardThumbnail},
	BOARD_REGDATE=#{boardRegdate}, BOARD_HEAD=#{boardHead}
where BOARD_NO=#{boardNo} and AUTHORITY_NO=#{authorityNo}
