mapper.select:
SELECT TO_CHAR(b.dt, 'YYYY-MM-DD') AS ordersDay , NVL(SUM(a.ordersCount), 0) ordersCount FROM ( select count(*) ordersCount, to_char(to_date(orders_date)+orders_duration/(24*60),'YYYY-MM-DD') as ordersDay from orders where to_date(orders_date)+orders_duration/(24*60) between trunc(sysdate-7,'IW') and trunc(sysdate-7,'IW')+7 and store_no=#{storeNo} and o_status_no=4 group by to_char(to_date(orders_date)+orders_duration/(24*60),'YYYY-MM-DD') order by to_char(to_date(orders_date)+orders_duration/(24*60),'YYYY-MM-DD') ) a,
( SELECT to_date(trunc(sysdate-7,'IW')) + LEVEL - 1 AS dt FROM dual CONNECT BY LEVEL <= (to_date(trunc(sysdate-7,'IW'))+6 - to_Date(trunc(sysdate-7,'IW')) + 1)
) b WHERE b.dt = a.ordersDay(+) GROUP BY TO_CHAR(b.dt, 'YYYY-MM-DD') ORDER BY TO_CHAR(b.dt, 'YYYY-MM-DD')
